import React from 'react';

const BeerInfo = (props) => {
  const info = props.location.state.beerDetail
  return(
    <div>
      <img src={info.image_url} style={{height: "10rem"}} alt="beer"/>
      <h5>{info.name}</h5>
      <p>{info.tagline}</p>
      <p>{info.description}</p>

    </div>
 )
}

export default BeerInfo;
