import React, {Component} from 'react';

class NewBeer extends Component {
  constructor(props){
    super(props)

    this.state = {
      name: '',
      tagline: '',
      description: '',
      first_brewed: '',
      brewers_tips: '',
      attenuation_level: '',
      contributed_by: ''
    }
  }

  handleInput(e){
    let {name, value} = event.target;
    this.setState({
      [name]: value
    })
  }

  render(){
    return(
      <div>
        <form>
          <label>Name:</label>
          <input type="text" name="name" value={this.state.name} onChange={(e) => this.handleInput(e)}/>
          <label>Tagline:</label>
          <input type="text" name="tagline" value={this.state.tagline}/>
          <label>Description:</label>
          <input type="text" name="description" value={this.state.description}/>
          <label>First Brewed:</label>
          <input type="text" name="first_brewed" value={this.state.first_brewed}/>
          <label>Attenuation Level:</label>
          <input type="number" name="attenuation_level" value={this.state.attenuation_level}/>
          <label>Contributed:</label>
          <input type="text" name="contributed_by" value={this.state.contributed_by}/>

          <input type="submit" value="Submit"/>
        </form>
      </div>
    )
  }
}

export default NewBeer;
