import React, {Component, Fragment} from 'react';
import BeerList from './BeerList';
import Search from './Search';


class AllBeers extends Component{
  constructor(props){
    super(props)

    this.state = {
      q: '',
      filteredData: [...this.props.location.state.beersData]
    }
  }

  searchHandler(e) {
    let { name, value } = e.target;
    this.setState({
      [name]: value
    })
    this.filterHandler(value);
  }

  filterHandler(search){
    const filter = [...this.props.location.state.beersData].filter((elem) => {
      return elem.name.includes(search)
    });

    this.setState({
      filteredData: filter
    });
  }

  render(){
    return(
      <Fragment>
      <Search search={this.state.q} searchHandler={(e) => {this.searchHandler(e)}}/>
      <ul className="list-unstyled">
    {
        props.location.state.beersData.map((beer, index) => {
       return <BeerList key={index} beerInfo={beer}> </BeerList>
      })
    }
      </ul>
      </Fragment>
    )
  }
}

export default AllBeers;
